<script>
import {email, required} from 'vuelidate/lib/validators'
import axios from "axios";
// import {TOKEN} from "@/api/common";

export default {
  name: "login",
  data() {
    return {
      logMessage: false,
      reqText: 'Поле обязательно для заполнения',
      reqTextMin: 'обязательно',
      LoginText: 'Не похоже на почту',
      login: ''
    }
  },
  computed: {
    logBtn() {
      return this.$v.login.$invalid
    },
  }, methods: {
    userLogin() {
      this.authenticate()
    },
    authenticate() {
      axios.post('https://nesaweb.xyz/registration-authorization/recovery/password', {
        email: this.login,
      })
          .then(function (response) {
            console.log(response);
            this.$router.push({name: 'login'})
          })
          .catch(function (error) {
            console.log(error);
          });
    }
  },
  validations: {
    login: {
      required,
      email
    }
  },
}
</script>

